import React, { useState, useEffect, useRef } from 'react';
import { throttle } from "lodash";
import Carts from './Carts';

export default function Data() {

  const [ value, setValue] = useState("")
  const [loader, setloader] = useState(false)
  const [cardsArr, setcardsArr] = useState([])
  let results = []

  useEffect(() => throttled.current(value), [value]);

  const throttled = useRef(throttle((newValue = "a") => {
    if (newValue) {
      getDataByFilter(newValue)
    }

  }, 3000));

  function getDataByFilter(value) {

    setloader(true)
    if (localStorage.getItem("actors") && value === "a") {

      setcardsArr(JSON.parse(localStorage.getItem("actors")))
      setloader(false)
      return
    }

    fetch('api/3070399882980494/search/' + value).then(function (response) {
      return response.json().then(function (response) {

        if (response.results && response.results.length > 0) {

          results = response.results.map((item) => { return { ...item.image, ...item.biography } });

          if (value === "a") {
            localStorage.setItem("actors", JSON.stringify(results))
          }
          setcardsArr(results)
          setloader(false)
        }

      })
    });
  }

  useEffect(() => { getDataByFilter("a") }, []);





return (

    <Carts/>

)







};
