import React ,{useState,useEffect} from 'react';
import { render } from 'react-dom';
import './style.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Data from "./ui-component/cards/data"



export default function App() {
  const [actorsArr, setActorsArr] = useState({});


  var apiRequest1 = fetch('api/3070399882980494/1/image').then(function(response){ 
    return response.json()
});
var apiRequest2 = fetch('api/3070399882980494/1/biography').then(function(response){
    return response.json()
});

var combinedData = {"apiRequest1":{},"apiRequest2":{}};

  useEffect(() => {


    Promise.all([apiRequest1,apiRequest2]).then(function(values){
      combinedData["apiRequest1"] = values[0];
      combinedData["apiRequest2"] = values[1];

      setActorsArr(combinedData["apiRequest1"] )
      //return combinedData;
  });
    fetch("api/3070399882980494/1/image", {
      "method": "GET",

      }).then(response => response.json())
              .then(parsedJSON => {

                console.log(parsedJSON)
                  setActorsArr(parsedJSON )
              })
              .catch(error => console.log(error));

  },[])

  return (
    <div>


      <Data data={actorsArr}/>

    </div >

  )

};


render(<App />, document.getElementById('root'));
