import React from 'react';


export default class Details extends React.Component {

    constructor(props) {
        super(props)

        this.detailsInfo = this.props.data.filter((item) => {
            if (item.id.toString() === this.props.match.params.id) {
                return true
            }
        })
        this.detailsInfo[0]

    }


    render() {
        return (


            <Card style={{ width: '18rem', margin: "10px", display: "inline-block" }}>

                {data.url ? <Card.Img variant="top" src={data.url} /> : <Card.Img variant="top" src="https://www.superherodb.com/pictures2/portraits/10/100/1508.jpg" />}
                <Card.Body>
                    <Card.Title>{data.name}</Card.Title>
                    <Card.Text>
                        {data["full-name"]}
                        <br></br>
                        {data["place-of-birth"]}
                        <br></br>
                        {data.publisher}

                    </Card.Text>
                    <Button variant="primary" >More Details</Button>
                </Card.Body>
            </Card> 


        );

    }

}
