import "./navbar.css"
import React, { useState, useEffect, useRef } from 'react';
import { throttle } from "lodash";
import Data from '../cards/data'


export default function Navbar() {

  const [value, setValue] = useState("")
  const [cardsArr, setcardsArr] = useState([])

  let results=[]







  useEffect(() => throttled.current(value), [value]);

  const throttled = useRef(throttle((newValue) => {
    if (newValue) {
      getDataByFilter(newValue)
    }

  }, 1000));

  function getDataByFilter(value) {
    fetch('api/3070399882980494/search/' + value).then(function (response) {
      return response.json().then(function (response) {

        if (response.results && response.results.length > 0) {

         results= response.results.map((item)=>{return {...item.image , ...item.biography}});  
         setcardsArr(results)
        }

      })
    });
  }


  //getDataByFilter("all")


  return (
    <div className="form-group has-success has-feedback">
      <label className="col-sm-2 control-label" for="inputSuccess">Search</label>
      <div className="col-sm-10">
        <input type="text" className="form-control" id="inputSuccess" onChange={(e) => setValue(e.target.value)} />
        <span className="glyphicon glyphicon-ok form-control-feedback"></span>
      </div>

      {<Data id={cardsArr.id} data={cardsArr}/>}
    </div>
  )

}
