import "./navbar.css"
import React, { useState, useEffect, useRef } from 'react';
import { throttle } from "lodash";
import Data from '../cards/data'


export default function Navbar() {

  const [value, setValue] = useState("")
  let dataCard=""
  function getDataGrid(res){

    dataCard= res.map((item)=><Data id={item.id} data={{...item.image , ...item.biography}} />)

    }






  useEffect(() => throttled.current(value), [value]);

  const throttled = useRef(throttle((newValue) => {
    if (newValue) {
      getDataByFilter(newValue)
    }

  }, 1000));

  function getDataByFilter(value) {
    fetch('api/3070399882980494/search/' + value).then(function (response) {
      return response.json().then(function (response) {

        if (response.results && response.results.length > 0) {

          getDataGrid(response.results)

        }

      })
    });
  }


  //getDataByFilter("all")


  return (
    <div class="form-group has-success has-feedback">
      <label class="col-sm-2 control-label" for="inputSuccess">Search</label>
      <div class="col-sm-10">
        <input type="text" class="form-control" id="inputSuccess" onChange={(e) => setValue(e.target.value)} />
        <span class="glyphicon glyphicon-ok form-control-feedback"></span>
      </div>
     {dataCard.length>0 ? <dataCard/>:""}

    </div>
  )

}
