import "./navbar.css"
import React ,{useState,useEffect,useRef}from 'react';
import { throttle } from "lodash";
import Data from ".../ui-component/cards/data"


export default function Navbar() {

  const [actorsArr, setActorsArr] = useState({});


  var actorsData = fetch('api/3070399882980494/1/image').then(function(response){ 
    return response.json()
});
var apiRequest2 = fetch('api/3070399882980494/1/biography').then(function(response){
    return response.json()
});



  useEffect(() => {
    Promise.all([actorsData,apiRequest2]).then(function(values){
      setActorsArr({...values[0],...values[1]})

    });   

  },[])
  function getDataByFilter(value){
    fetch('api/3070399882980494/search/'+value).then(function(response){
    return response.json().then(function(response){
      if(response && response.results && response.length>0){
        console.log(response.results[0].image)
        setActorsArr(response.results[0].image)
      }

    })
});
  }
  const [value, setValue] = useState("")
  const throttled = useRef(throttle((newValue) => {
    if(newValue){
      getDataByFilter(newValue)
    }

  }, 1200))

  useEffect(() => throttled.current(value), [value])

  return (
    <div class="form-group has-success has-feedback">
      <label class="col-sm-2 control-label" for="inputSuccess">Search</label>
      <div class="col-sm-10">
        <input type="text" class="form-control" id="inputSuccess"  onChange={(e) => setValue(e.target.value)}/>
        <span class="glyphicon glyphicon-ok form-control-feedback"></span>
      </div>

      <Data data={actorsArr}/> 
    </div>
  )

}
