import React ,{ useState, useEffect, useRef } from 'react';
import  './card.css'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import {
  BrowserRouter as Router,
  Link,

} from "react-router-dom";


export default function Carts({data}) {
 // let { url } = useRouteMatch();


  const [ value, setValue] = useState("")
  const [loader, setloader] = useState(false)
  const [cardsArr, setcardsArr] = useState([])
  let results = []

  useEffect(() => throttled.current(value), [value]);

  const throttled = useRef(throttle((newValue = "a") => {
    if (newValue) {
      getDataByFilter(newValue)
    }

  }, 3000));

  function getDataByFilter(value) {

    setloader(true)
    if (localStorage.getItem("actors") && value === "a") {

      setcardsArr(JSON.parse(localStorage.getItem("actors")))
      setloader(false)
      return
    }

    fetch('api/3070399882980494/search/' + value).then(function (response) {
      return response.json().then(function (response) {

        if (response.results && response.results.length > 0) {

          results = response.results.map((item) => { return { ...item.image, ...item.biography } });

          if (value === "a") {
            localStorage.setItem("actors", JSON.stringify(results))
          }
          setcardsArr(results)
          setloader(false)
        }

      })
    });
  }

  useEffect(() => { getDataByFilter("a") }, []);







  let fullName = data["full-name"];
  let birthPlace=data["place-of-birth"]

  return (

  <Router>

    <Link  to={{
                                pathname: "actors/1",
                                state: {
                                    fromNavBar: true
                                }
                            }} >


<div className="form-group has-success has-feedback">
      <label className="col-sm-2 control-label" for="inputSuccess">Search</label>
          <div className="col-sm-12">
            <input type="text" className="form-control" id="inputSuccess" onChange={(e) => setValue(e.target.value)} />
            <span className="glyphicon glyphicon-ok form-control-feedback"></span>
          </div>

          {!loader ? cardsArr.map((data)=><Card  style={{ width: '18rem',margin:"10px",display:"inline-block"}}>

                {data.url ?<Card.Img variant="top"  src={data.url} />:<Card.Img variant="top"  src="https://www.superherodb.com/pictures2/portraits/10/100/1508.jpg" />}
                <Card.Body>
                  <Card.Title>{data.name}</Card.Title>
                  <Card.Text>
                    {fullName}
                  <br></br>
                    {birthPlace}
                    <br></br>
                  {data.publisher}

                </Card.Text>
                  <Button variant="primary" >More Details</Button>
                </Card.Body>
              </Card>):<div className="loader"></div>}


    </div>


        </Link>


      </Router>
    )






}
